import React, { Component } from 'react'
import Addtodo_main from '../addTodo_main';
import PropTypes from 'prop-types';

export default class nus_background extends Component {
    state = {
        title: ''
    }

    onChange = (e) => this.setState({[e.target.name]: e.target.value})
    onSubmit = (e) => {
        e.preventDefault();
        this.props.addTodo(this.state.title);
        this.setState({title: ''});
    }
    render() {
        return (
            <div className = "nus_background" id = "nus">
                <footer style = {footStyle}>
                    <h6>𝕎𝕖𝕝𝕔𝕠𝕞𝕖 𝕥𝕠 ℕ𝕌𝕊</h6>
                    <h7>World class University and cuisine paradise!</h7>
                </footer> 
                <form onSubmit = {this.onSubmit} className = "submitForm"> 
                <input 
                    type = "text" 
                    name = "title" 
                    style ={{width: '50%', padding: '5px'}}
                    placeholder = "Search for your crave"
                    value = {this.state.title}
                    onChange = {this.onChange}
                />
                <input 
                type="submit" 
                value = "Submit" 
                className ="btn"
                style = {{width: '10%'}}
                />
            </form>
            </div>
        )
    }
}

const footStyle = {
    color: '#000000',
    textAlign: 'center',
    padding: '10px',
}
//Proptypes
nus_background.propTypes = {
    nus_background: PropTypes.func.isRequired,
}
